class GenericError {
  constructor (props) {
    this.message = 'There was an issue with processing your request.'
    this.errorCode = 84
    this.errorDisplay = 'errorPage'
    this.statusCode = 500
    Object.assign(this, props)
  }

  static withErrorMessage (errObject) {
    return new GenericError({
      message: `There was an issue with processing your request. Error: ${errObject.message}`
    })
  }
}
exports.GenericError = GenericError
