class GBP {
  constructor (props) {
    let { pence, pounds } = props
    if (!pounds) {
      pounds = 0
    }
    if (!!pence) {
      pounds += Math.floor(
        (pence / 100)
      )
      pence %= 100
      pence = Math.ceil(pence)
    } else {
      pence = 0
    }
    Object.assign(this, { pence, pounds })
  }

  get poundVal () {
    return this.pounds + this.pence / 100
  }

  get pennyVal () {
    return Math.round(this.pounds * 100 + this.pence)
  }

  static toPounds (pence) {
    return new GBP({ pence }).poundVal
  }

  static toPence (pounds) {
    return new GBP({ pounds }).pennyVal
  }
}
exports.GBP = GBP