class Name {
  constructor (props) {
    Object.assign(this, props)
  }

  get full () {
    const { givenName, middleName, surname } = this
    return { givenName, middleName, surname }
  }

  get legal () {
    const { givenName, surname } = this
    return ( surname != null ) ? `${givenName} ${surname}` : givenName
  }
}

module.exports = {
  Name
}
