const { Q } = require('pp-mysql/client').shorthands();
const { isNumber, isString, isArray } = require('./model');
const { LoanTrxBase } = require('./loantrxbase');

/// Loan transactions
class LoanTrx extends LoanTrxBase {
    static joinTrx() {
        return `INNER JOIN ${this.jsonTable()} AS ${Q(this.jsontable)}`;
    };
}

module.exports = { LoanTrx };
